package Homework;

public class factorial_DoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int factorial=1;
		int i = 1;
		do {
			factorial = factorial*i;
			System.out.println(factorial);
			i++;
		   }
		
		while (i<=7);
		
		

	}

}
