package com.zaman.test1;

public class problem_1_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int day = 24*60;
		int week = day*7;
		int year = 12*week;
		
		for (int i=day; i<=year; i++)
			
		{
			if (year<=365);
			System.out.println("Yearly total inco: "+i);
			break;
		}
	}
}
